/*!
 *	== LOADIMAGE ==
 *	This plugin will load an image into a container div.  It will
 *	display a "loader" image while the primary images loads.
 *
 *	@author Ryan Provost (ryan_provost@anninc.com)
 *	@created 2011-11-29
 */
 (function(a){a.fn.loadImage=function(b){var c=this;if(this.find(".display").attr("src")!=b.path){this.html("");this.css({overflow:"hidden"});var d={loader:
"http://upload.wikimedia.org/wikipedia/commons/d/de/Ajax-loader.gif",speed:150};if(b)a.extend(d,b);this.append("<img class='loadImage display' />");
this.append("<img class='loadImage loader' src='"+d.loader+"'/>");if(d.title!==undefined)this.find(".display").attr("title",d.title);
if(d.alt!==undefined)this.find(".display").attr("alt",d.alt);this.find(".display").hide();this.find(".loader").css({position:"absolute",top:this.height()/2-16,
left:this.width()/2-16,"z-index":2});this.find(".display").attr("src",d.path).load(function(){c.find(".loader").hide();c.find(".display").show()});
if(d.over!==undefined&&d.over.length>0){this.append("<img class='loadImage over' src='"+d.over+"'/>");this.find(".over").css({position:"absolute",
cursor:"pointer",top:0,left:0,"z-index":1});this.find(".over").hide();this.find(".display").mouseenter(function(){c.find(".over").fadeIn(d.speed)});
this.find(".over").mouseleave(function(){c.find(".over").fadeOut(d.speed)})}}return this}})(jQuery)